import { useEffect,useState } from 'react';
import axios from 'axios';
import { Button } from 'react-bootstrap';

function Posts(){

    const [posts, setPosts] = useState(); 

    useEffect(() => {
        // console.log("useEffect");

        axios
            .get("/post")
            .then((res) => {
                console.log(res.data);
                setPosts(res.data);
            })
            .catch((err)=> console.log(err));
    },[])

    const deletePost = (id) => {
        console.log(id);
    };

    return(
        <div className="posts" style={{ width: "90%" , margin: "auto auto", textAlign:"center"}} >
            <h1>Posts Page</h1>
          {posts? (
            <>
                {posts.map((post) => {
                    return(
                        <div 
                        style={{ 
                            border: "1px lightgray solid",
                            padding: "1rem",
                            marginBottom: "1rem",
                            borderRadius: "10px",
                        }}>
                            <h4>{post.title}</h4>
                            <p>{post.description}</p>
                            <div style={{
                                display: "flex",
                                justifyContent: "space-between",

                            }}>
                                <Button style={{ margin: "1rem", width: "100%"}}
                                variant="outline-info">UPDATE</Button>
                                <Button style={{ margin: "1rem", width: "100%"}}
                                variant="outline-danger" onClick={()=> deletePost(post._id)}>DELETE</Button>
                            </div>
                        </div>
                    )
                })}
            </>
          ): ""}
        </div>
    );
}

export default Posts;
