import { useEffect,useState } from 'react';
import axios from 'axios';

function Posts(){

    const [posts, setPosts] = useState(); 

    useEffect(() => {
        console.log("useEffect");

        axios
            .get("/post")
            .then((res) => {
                console.log(res.data);
                setPosts(res.data);
            })
            .catch((err)=> console.log(err));
    },[])
    return(
        <div className="posts" style={{ width: "90%" , margin: "auto auto", textAlign:"center"}} >
            Posts Page
          {posts? (
            <>
                {posts.map((post) => {
                    return(
                        <div>
                            <h4>{post.title}</h4>
                            <p></p>
                        </div>
                    )
                })}
            </>
          ): ""}
        </div>
    );
}

export default Posts;
