import { useEffect,useState } from 'react';
import axios from 'axios';
import { Button } from 'react-bootstrap';
import {useNavigate} from 'react-router-dom';
import Modal from 'react-bootstrap/Modal';
function Posts(){
    const navigate = useNavigate();
    const [posts, setPosts] = useState(); 

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    useEffect(() => {
        // console.log("useEffect");

        axios
            .get("/post")
            .then((res) => {
                console.log(res.data);
                setPosts(res.data);
            })
            .catch((err)=> console.log(err));
    },[])

    const deletePost = (id) => {
        // console.log(id);

        axios
            .delete(`/delete/${id}`)
            .then((res) => console.log(res))
            .catch((err) => console.log(err));

        window.location.reload();
    };

    return(
        <div className="posts" style={{ maxwidth: "50%" , margin: "auto auto", textAlign:"center",}} >
            <h1>Posts Page</h1>
            <Button style={{ width: "50%", marginBottom: "10px"}} variant="outline-dark"onClick={() =>navigate(-1)}>BACK</Button>
          {posts? (
            <>
                {posts.map((post) => {
                    return(
                        <div className='post'
                        key={post._id}
                        style={{ 
                            border: "1px lightgray solid",
                            padding: "1rem",
                            marginBottom: "10px",
                            borderRadius: "10px",
                            margin: "auto auto", 
                            textAlign:"center",
                            width: "50%",
                            height: "160px",
                        }}>
                            <h4>{post.title}</h4>
                            <p>{post.description}</p>
                            <div style={{
                                display: "flex",
                                justifyContent: "space-between",

                            }}>
                                <Button style={{ margin: "1rem", width: "100%"}}
                                variant="outline-info">UPDATE</Button>
                                <Button style={{ margin: "1rem", width: "100%"}}
                                variant="outline-danger" onClick={()=> deletePost(post._id)}>DELETE</Button>
                            </div>
                        </div>
                    )
                })}
            </>
          ): ""}
        </div>
    );
}

export default Posts;
