import { useEffect,useState } from 'react';
import axios from 'axios';
import { Button } from 'react-bootstrap';

function Posts(){

    const [posts, setPosts] = useState(); 

    useEffect(() => {
        console.log("useEffect");

        axios
            .get("/post")
            .then((res) => {
                console.log(res.data);
                setPosts(res.data);
            })
            .catch((err)=> console.log(err));
    },[])
    return(
        <div className="posts" style={{ width: "90%" , margin: "auto auto", textAlign:"center"}} >
            <h1>Posts Page</h1>
          {posts? (
            <>
                {posts.map((post) => {
                    return(
                        <div style={{ 
                            border: "1px solid black",
                            padding: "1rem",
                            marginBottom: "1rem",
                            borderRadius: "10px",
                        }}>
                            <h4>{post.title}</h4>
                            <p>{post.description}</p>
                            <Button style={{
                                padding: "0.5rem",
                            }} varient="outline-success">Update</Button>
                            <Button varient="outline-failure">Delete</Button>
                        </div>
                    )
                })}
            </>
          ): ""}
        </div>
    );
}

export default Posts;
