        console.log("useEffect");

        axios
            .get("/post")
            .then((res) => {
                console.log(res.data);
                setPosts(res.data);
            })
            .catch((err)=> console.log(err));
    },[])
    return(
        <div className="posts" style={{ width: "90%" , margin: "auto auto", textAlign:"center"}} >
            Posts Page
          {posts? (
            <>
                {posts.map(post)}
            </>
          ): ""}
        </div>
    );
}

export default Posts;
